# Find the index of the first fib number with 1000 digits

num1 = 1 #first number in the sequence
num2 = 1 #second number in the sequence
count = 2 #the first 2 numbers are 1
while(len(str(num2)) < 1000):
    # loop until the new/bigger number has fewer than 1000 digits
    new_num = num1 + num2 #next number in the sequence
    num1 = num2 #update num1 to be what num2 was
    num2 = new_num #num2 is now the new sum
    count += 1
print(count)
print(num2)

end_time = time.time() #get our ending time
print(f"--- Number of seconds to solve {time.time() - start_time}")

